import { useEffect, useState } from "react";

export default function Password() {
  const [Password, setPassword] = useState("")
  const [color, setColor] = useState({
    seven: "red",
    upp: "red",
    low: "red",
    num: "red",
    spl: "red"
  })
  useEffect(() => {
    setColor({
      seven:Password.length>7?"green":"red",
      upp: /[A-Z]/g.test(Password)?"green":"red",
      low: /[a-z]/g.test(Password)?"green":"red",
      num: /[0-1]/g.test(Password)?"green":"red",
      spl: /[[!@#$%^&*]]/g.test(Password)?"green":"red"
    })
  }, [Password])
  return (
    <>
      <main>
        <input type="password" onChange={(e) => { setPassword(e.target.value) }} />
        <ul>
          <li style={seven?color.seven:color.seven}>Contains Seven Or More</li>
          <li>Contains Upper</li>
          <li>Contains Lower</li>
          <li>Containss Number</li>
          <li>Contains Special</li>
        </ul>
      </main>
      {Password}
    </>
  )
}
